import {Card, CardBody, CardTitle, CardSubtitle, Table, Input, Label} from "reactstrap";
import user1 from "../../assets/images/users/user1.jpg";
import user2 from "../../assets/images/users/user2.jpg";
import user3 from "../../assets/images/users/user3.jpg";
import user4 from "../../assets/images/users/user4.jpg";
import user5 from "../../assets/images/users/user5.jpg";
import {useState} from "react";

const tableData = [
  {
   // avatar: user1,
    id: "System1",
    email: "CentOS",
    project: "192.168.10.1",
    weeks: "35",
    admin: "-",
    budget: "2023-11-01",
    status: "N",
  },
  {
   // avatar: user2,
    id: "System2",
    email: "RedHat",
    project: "192.168.10.2",
    weeks: "35",
    admin: "-",
    budget: "2023-11-02",
    status: "Y",
  },
  {
  //  avatar: user3,
    id: "System3",
    email: "Windows",
    project: "192.168.10.3",
    weeks: "4",
    admin: "3",
    budget: "2023-11-06",
    status: "Y",
  },
  {
  //  avatar: user4,
    id: "System4",
    email: "MSSQL",
    project: "192.168.10.4",
    weeks: "352",
    admin: "4",
    budget: "2023-11-06",
    status: "N",
  },
  {
  //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "2",
    admin: "2",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "6",
    admin: "1",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "7",
    admin: "5",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "127",
    admin: "3",
    budget: "2023-11-07",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "53",
    admin: "-",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "42",
    admin: "-",
    budget: "2023-11-08",
    status: "N",
  },
  {
    //  avatar: user5,
    id: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "35",
    admin: "-",
    budget: "2023-11-06",
    status: "N",
  },

];
const [checkItems, setCheckItems] = useState([])

const checkHandled = (e) => {
  console.log('checkHandled')
  checkItemHandler(e.target.id, e.target.checked);
}

const checkItemHandler = (id, isChecked) => {
  if (isChecked) {
    setCheckItems((prev) => [...prev, id]) // 불변성을 지키기 위한 원본 배열을 복사 후 추가
  } else {
    setCheckItems(checkItems.filter((item) => item !== id))
    // 현재 checkItems의 배열에서 해당 id를 제외한 새로운 배열 반환
  }
}

const allCheckedHandler = (e) => {
  if (e.target.checked) {
    setCheckItems(tableData.map((item) => item.id))
  } else {
    setCheckItems([]);
  }
  console.log(`allCheck = `, e.target.checked)
}
const ProjectTables = () => {
  return (
    <div>
      <Card>
        <CardBody>
          {/*<CardTitle tag="h5">Project Listing</CardTitle>*/}
          {/*<CardSubtitle className="mb-2 text-muted" tag="h6">*/}
          {/*  Overview of the projects*/}
          {/*</CardSubtitle>*/}

          <Table className="no-wrap mt-3 align-middle text-center" Table bordered hover >
            <thead>
              <tr>
                <th><Input type="checkbox" />  onChange={allCheckedHandler}
                  checked={checkItems.length === tableData.length ? true : false}/>
                  </th>
                <th>시스템명</th>
                <th>시스템유형</th>
                <th>IP</th>
                <th>계정수</th>
                <th>관리자</th>
                <th>동기화일자</th>
                <th>동기화여부</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((tdata, index) => (
                <tr key={index} className="border-top">
                  <td>
                    <label>
                      <input id={id} type="checkbox" checked={checked} onChange={checkHandled}/>
                      {id}
                    </label>

                    {
                    checkList.map((item) => (
                        <CheckBox key={item.id} id={item.id} checkItemHandler={checkItemHandler}
                                  checked={checkItems.includes(item.id) ? true : false} />
                    ))
                  }</td>

                  <td>
                      {/*<img*/}
                      {/*  src={tdata.avatar}*/}
                      {/*  className="rounded-circle"*/}
                      {/*  alt="avatar"*/}
                      {/*  width="45"*/}
                      {/*  height="45"*/}
                      {/*/>*/}
                        {tdata.id}
                  </td>
                  <td>{tdata.email}</td>
                  <td>{tdata.project}</td>
                  <td>{tdata.weeks}</td>
                  <td>{tdata.admin}</td>
                  <td>{tdata.budget}</td>
                  <td>
                    {tdata.status === "N" ? (
                        <span className="p-2 bg-danger rounded-circle d-inline-block"></span>
                    ) : (
                        <span className="p-2 bg-success rounded-circle d-inline-block"></span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </CardBody>
      </Card>
    </div>
  );
};

export default ProjectTables;
