import {Card, CardBody, CardTitle, CardSubtitle, Table, Input, Label} from "reactstrap";
import user1 from "../../assets/images/users/user1.jpg";
import user2 from "../../assets/images/users/user2.jpg";
import user3 from "../../assets/images/users/user3.jpg";
import user4 from "../../assets/images/users/user4.jpg";
import user5 from "../../assets/images/users/user5.jpg";

const tableData = [
  {
   // avatar: user1,
    name: "System1",
    email: "CentOS",
    project: "192.168.10.1",
    weeks: "35",
    admin: "-",
    budget: "2023-11-01",
    status: "N",
  },
  {
   // avatar: user2,
    name: "System2",
    email: "RedHat",
    project: "192.168.10.2",
    weeks: "35",
    admin: "-",
    budget: "2023-11-02",
    status: "Y",
  },
  {
  //  avatar: user3,
    name: "System3",
    email: "Windows",
    project: "192.168.10.3",
    weeks: "4",
    admin: "3",
    budget: "2023-11-06",
    status: "Y",
  },
  {
  //  avatar: user4,
    name: "System4",
    email: "MSSQL",
    project: "192.168.10.4",
    weeks: "352",
    admin: "4",
    budget: "2023-11-06",
    status: "N",
  },
  {
  //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "2",
    admin: "2",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "6",
    admin: "1",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "7",
    admin: "5",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "127",
    admin: "3",
    budget: "2023-11-07",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "53",
    admin: "-",
    budget: "2023-11-06",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "42",
    admin: "-",
    budget: "2023-11-08",
    status: "N",
  },
  {
    //  avatar: user5,
    name: "System5",
    email: "Oracle",
    project: "192.168.10.5",
    weeks: "35",
    admin: "-",
    budget: "2023-11-06",
    status: "N",
  },

];


const CheckboxContext = createContext();

// 체크된 아이템을 담을 배열
const [checkItems, setCheckItems] = useState([]);

// 체크박스 단일 선택
const handleSingleCheck = (checked, id) => {
  if (checked) {
    // 단일 선택 시 체크된 아이템을 배열에 추가
    setCheckItems(prev => [...prev, id]);
  } else {
    // 단일 선택 해제 시 체크된 아이템을 제외한 배열 (필터)
    setCheckItems(checkItems.filter((el) => el !== id));
  }
};

// 체크박스 전체 선택
const handleAllCheck = (checked) => {
  if(checked) {
    // 전체 선택 클릭 시 데이터의 모든 아이템(id)를 담은 배열로 checkItems 상태 업데이트
    const idArray = [];
    data.forEach((el) => idArray.push(el.id));
    setCheckItems(idArray);
  }
  else {
    // 전체 선택 해제 시 checkItems 를 빈 배열로 상태 업데이트
    setCheckItems([]);
  }
}



const ProjectTables = () => {
  return (
    <div>
      <Card>
        <CardBody>
          {/*<CardTitle tag="h5">Project Listing</CardTitle>*/}
          {/*<CardSubtitle className="mb-2 text-muted" tag="h6">*/}
          {/*  Overview of the projects*/}
          {/*</CardSubtitle>*/}

          <Table className="no-wrap mt-3 align-middle text-center" Table bordered hover >
            <thead>
              <tr>
                <th><Input type="checkbox" /></th>
                <th>시스템명</th>
                <th>시스템유형</th>
                <th>IP</th>
                <th>계정수</th>
                <th>관리자</th>
                <th>동기화일자</th>
                <th>동기화여부</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((tdata, index) => (
                <tr key={index} className="border-top">
                  <td><Input type="checkbox" name='select-all'
                             onChange={(e) => handleAllCheck(e.target.checked)}
                      // 데이터 개수와 체크된 아이템의 개수가 다를 경우 선택 해제 (하나라도 해제 시 선택 해제)
                             checked={checkItems.length === data.length ? true : false} />

                  </td>

                  <td>
                      {/*<img*/}
                      {/*  src={tdata.avatar}*/}
                      {/*  className="rounded-circle"*/}
                      {/*  alt="avatar"*/}
                      {/*  width="45"*/}
                      {/*  height="45"*/}
                      {/*/>*/}
                        {tdata.name}
                  </td>
                  <td>{tdata.email}</td>
                  <td>{tdata.project}</td>
                  <td>{tdata.weeks}</td>
                  <td>{tdata.admin}</td>
                  <td>{tdata.budget}</td>
                  <td>
                    {tdata.status === "N" ? (
                        <span className="p-2 bg-danger rounded-circle d-inline-block"></span>
                    ) : (
                        <span className="p-2 bg-success rounded-circle d-inline-block"></span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </CardBody>
      </Card>
    </div>
  );
};

export default ProjectTables;
