import {
  Card,
  Row,
  Col,
  CardTitle,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  // FormText
} from "reactstrap";
import { useState } from "react";


import { login } from "../api/auth/AuthAPI";

export default function SignInPage() {

  const [values, setValues] = useState({
    username: "",
    password: "",
  });

  const handleChange = async (e) => {
    setValues({...values,
      [e.target.id]: e.target.value,
    });
  }

  const handleSubmit = async (e) => {
    login(values)
        .then((response) => {
          localStorage.clear();
          localStorage.setItem('tokenType', response.tokenType);
          localStorage.setItem('accessToken', response.accessToken);
          localStorage.setItem('refreshToken', response.refreshToken);
          window.location.href = `/home`;
        }).catch((error) => {
      console.log(error);
    });
  }
  const Loginpage = () => {
    return (
        <div style={{margin:'150px',paddingLeft:'300px',paddingRight:'300px'}}>
          <Row>
            <Col>
              <Card>
                <CardTitle tag="h6" className="border-bottom p-3 mb-0">
                  <i className="bi bi-bell me-2"> </i>
                  IM 로그인페이지
                </CardTitle>
                <CardBody>
                  <Form>
                    <FormGroup>
                      <Label for="exampleId">ID</Label>
                      <Input
                          id="username" onChange={handleChange} value={values.username}


                          name="email"
                          placeholder="아이디"
                          type="email"
                      />
                    </FormGroup>
                    <FormGroup>
                      <Label for="examplePassword">PW</Label>
                      <Input
                          id="password" onChange={handleChange} value={values.password}


                          name="password"
                          placeholder="비밀번호"
                          type="password"
                      />
                    </FormGroup>
                    {/* <FormGroup>
              <Label for="exampleSelect">Select</Label>
              <Input id="exampleSelect" name="select" type="select">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </Input>
            </FormGroup>
            <FormGroup>
              <Label for="exampleSelectMulti">Select Multiple</Label>
              <Input
                id="exampleSelectMulti"
                multiple
                name="selectMulti"
                type="select"
              >
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </Input>
            </FormGroup> */}
                    <div className="button-group">
                      <Button className="btn" color="info" size="lg" block>로그인</Button>
                      <Button className="btn" outline color="success" size="sm" >회원가입</Button>
                      <Button className="btn" outline color="warning" size="sm" >계정 찾기</Button>
                    </div>

                  </Form>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>

    );
  };
}
